// Integration Tests: Order Service → Payment Service
// File: backend/services/order-service/tests/integration/order-to-payment.integration.test.ts

import request from 'supertest';
import { Kafka, Producer, Consumer } from 'kafkajs';
import prisma from '../../src/lib/prisma';

// Mock setup
const TEST_USER_TOKEN = 'Bearer test-jwt-token';
const TEST_USER_ID = '123e4567-e89b-12d3-a456-426614174000';
const API_BASE_URL = 'http://localhost:3002'; // Order Service

describe('Integration Tests: Order Service → Payment Service', () => {
  let kafkaProducer: Producer;
  let kafkaConsumer: Consumer;

  beforeAll(async () => {
    // Setup Kafka for testing
    const kafka = new Kafka({
      clientId: 'test-client',
      brokers: ['localhost:9092'],
    });

    kafkaProducer = kafka.producer();
    kafkaConsumer = kafka.consumer({ groupId: 'test-group' });

    await kafkaProducer.connect();
    await kafkaConsumer.connect();
    await kafkaConsumer.subscribe({ topic: 'order.create', fromBeginning: false });
  });

  afterAll(async () => {
    await kafkaProducer.disconnect();
    await kafkaConsumer.disconnect();
    await prisma.$disconnect();
  });

  afterEach(async () => {
    // Cleanup test data
    await prisma.orderItem.deleteMany({});
    await prisma.orderSession.deleteMany({});
    await prisma.order.deleteMany({});
  });

  // ==========================================
  // 1️⃣ HAPPY PATH TESTS
  // ==========================================

  describe('1. Happy Path Tests', () => {
    test('1.1: Create Order from Cart → Payment URL Generated Successfully', async () => {
      // ARRANGE
      const orderRequest = {
        storeId: 'store-123',
        deliveryAddress: '123 Nguyen Hue St, District 1, HCMC',
        contactPhone: '0901234567',
        note: 'No onions please',
      };

      // Setup Kafka listener for order.create event
      const kafkaMessages: any[] = [];
      kafkaConsumer.run({
        eachMessage: async ({ message }) => {
          kafkaMessages.push(JSON.parse(message.value?.toString() || '{}'));
        },
      });

      // ACT: Create order
      const response = await request(API_BASE_URL)
        .post('/api/orders/create-from-cart')
        .set('Authorization', TEST_USER_TOKEN)
        .send(orderRequest);

      // ASSERT: Response validation
      expect(response.status).toBe(201);
      expect(response.body.success).toBe(true);
      expect(response.body.data.orderId).toBeDefined();
      expect(response.body.data.status).toBe('pending');
      expect(response.body.data.session).toBeDefined();
      expect(response.body.data.session.status).toBe('active');
      expect(response.body.data.session.expiresAt).toBeDefined();

      const orderId = response.body.data.orderId;
      const sessionId = response.body.data.session.sessionId;

      // Wait for Kafka message
      await new Promise((resolve) => setTimeout(resolve, 2000));

      // ASSERT: Kafka event published
      expect(kafkaMessages.length).toBeGreaterThan(0);
      const kafkaEvent = kafkaMessages.find((msg) => msg.orderId === orderId);
      expect(kafkaEvent).toBeDefined();
      expect(kafkaEvent.userId).toBe(TEST_USER_ID);
      expect(kafkaEvent.totalPrice).toBeGreaterThan(0);
      expect(kafkaEvent.sessionId).toBe(sessionId);

      // ASSERT: Database verification
      const orderInDb = await prisma.order.findUnique({
        where: { id: orderId },
        include: { items: true },
      });
      expect(orderInDb).not.toBeNull();
      expect(orderInDb?.status).toBe('pending');
      expect(orderInDb?.userId).toBe(TEST_USER_ID);

      const sessionInDb = await prisma.orderSession.findUnique({
        where: { id: sessionId },
      });
      expect(sessionInDb).not.toBeNull();
      expect(sessionInDb?.status).toBe('active');
      expect(sessionInDb?.orderId).toBe(orderId);
      expect(sessionInDb?.paymentAttempts).toBe(0);
      expect(sessionInDb?.maxPaymentAttempts).toBe(3);

      // TODO: Add verification for payment.event from Payment Service
    });

    test('1.2: Create Order Directly (Not from Cart)', async () => {
