      const response = await request(API_BASE_URL)
        .post('/api/orders/create')
        .set('Authorization', TEST_USER_TOKEN)
        .send({
          items: [{ productId: 'prod-001', quantity: 1 }],
          deliveryAddress: '123 St',
          contactPhone: '0901234567',
        });

      const orderId = response.body.data.orderId;

      // Verify both exist
      const order = await prisma.order.findUnique({
        where: { id: orderId },
      });

      const session = await prisma.orderSession.findUnique({
        where: { orderId },
      });

      expect(order).not.toBeNull();
      expect(session).not.toBeNull();
      expect(session?.orderId).toBe(orderId);
    });

    test('6.3: Cascade Delete OrderSession when Order Deleted', async () => {
      // Create order
      const order = await prisma.order.create({
        data: {
          userId: TEST_USER_ID,
          totalPrice: 100000,
          deliveryAddress: '123 St',
          contactPhone: '0901234567',
          status: 'pending',
        },
      });

      const session = await prisma.orderSession.create({
        data: {
          orderId: order.id,
          sessionDurationMinutes: 15,
          expiresAt: new Date(Date.now() + 15 * 60 * 1000),
          status: 'active',
          paymentAttempts: 0,
          maxPaymentAttempts: 3,
        },
      });

      // Delete order
      await prisma.order.delete({
        where: { id: order.id },
      });

      // Verify session also deleted (cascade)
      const sessionAfterDelete = await prisma.orderSession.findUnique({
        where: { id: session.id },
      });

      expect(sessionAfterDelete).toBeNull();
    });
  });
});

