/*
Trong file k6-customer-flow.js, thay thế phần options:

// Import config
import { smokeTestOptions } from './config.js';

// Sử dụng
export let options = smokeTestOptions;

Hoặc trực tiếp inline:
export let options = {
    vus: 10,
    duration: '1m',
    thresholds: {
        http_req_duration: ['p(95)<2000'],
    },
};
*/

// ============================================
// CUSTOM ENVIRONMENT VARIABLES
// ============================================

/*
Các biến môi trường có thể set:

K6_BASE_URL=http://localhost:3000
K6_USER_PASS=Test@123456

Ví dụ:
K6_BASE_URL=http://api.example.com k6 run k6-customer-flow.js
*/

// ============================================
// THRESHOLDS EXPLAINED
// ============================================

/*
http_req_duration:
- p(95)<2000  → 95% requests phải < 2 giây
- p(99)<5000  → 99% requests phải < 5 giây
- avg<1000    → Trung bình < 1 giây
- med<800     → Median < 800ms
- max<10000   → Max < 10 giây

http_req_failed:
- rate<0.05   → < 5% requests thất bại
- rate<0.01   → < 1% requests thất bại

Custom metrics:
- login_success: ['rate>0.95']  → > 95% login thành công
- register_success: ['rate>0.90'] → > 90% register thành công

Iterations:
- iteration_duration: ['avg<10000'] → Mỗi iteration < 10s trung bình
*/

// ============================================
// TAGS - Để group metrics
// ============================================

/*
Trong request, thêm tags:

const params = {
    headers: { 'Content-Type': 'application/json' },
    tags: { 
        name: 'register_customer',
        type: 'auth',
        critical: 'yes'
    },
};

Sau đó có thể filter metrics theo tag:
http_req_duration{name:register_customer}
*/

#!/bin/bash
# K6 Load Test Runner Script # Chạy load test cho User Service với các profile khác nhau
set -e
# Colors RED='\033[0;31m' GREEN='\033[0;32m' YELLOW='\033[1;33m' BLUE='\033[0;34m' NC='\033[0m' # No Color
# Default values BASE_URL="${K6_BASE_URL:-http://localhost:3000}" TEST_FILE="k6-customer-flow.js"
# Check if k6 is installed if ! command -v k6 &> /dev/null; then echo -e "${RED}❌ k6 is not installed${NC}" echo -e "${YELLOW}Please install k6:${NC}" echo " MacOS: brew install k6" echo " Linux: See https://k6.io/docs/getting-started/installation/" exit 1 fi
echo -e "${GREEN}✓ k6 is installed${NC}" echo -e "${BLUE}Base URL: ${BASE_URL}${NC}" echo ""
# Function to run test run_test() { local profile=$1 echo -e "${YELLOW}Running ${profile} test...${NC}"
case $profile in smoke) echo -e "${BLUE}Smoke Test: 10 users for 1 minute${NC}" k6 run --vus 10 --duration 1m -e K6_BASE_URL=${BASE_URL} ${TEST_FILE} ;;
load) echo -e "${BLUE}Load Test: 100 users for 5 minutes${NC}" k6 run --vus 100 --duration 5m -e K6_BASE_URL=${BASE_URL} ${TEST_FILE} ;;
stress) echo -e "${BLUE}Stress Test: 500 users for 10 minutes${NC}" k6 run --vus 500 --duration 10m -e K6_BASE_URL=${BASE_URL} ${TEST_FILE} ;;
spike) echo -e "${BLUE}Spike Test: Rapid increase to 1000 users${NC}" k6 run --stage 0s:10,10s:1000,3m:1000,10s:10,30s:0 -e K6_BASE_URL=${BASE_URL} ${TEST_FILE} ;;
full) echo -e "${BLUE}Full Test: Default configuration (9.5 minutes)${NC}" k6 run -e K6_BASE_URL=${BASE_URL} ${TEST_FILE} ;;
*) echo -e "${RED}Unknown test profile: ${profile}${NC}" echo "Available profiles: smoke, load, stress, spike, full" exit 1 ;; esac }
# Show menu show_menu() { echo -e "${GREEN}==================================${NC}" echo -e "${GREEN}K6 Load Test - User Service${NC}" echo -e "${GREEN}==================================${NC}" echo "" echo "Select test profile:" echo " 1) Smoke Test (10 users, 1 min)" echo " 2) Load Test (100 users, 5 min)" echo " 3) Stress Test (500 users, 10 min)" echo " 4) Spike Test (0→1000 users spike)" echo " 5) Full Test (Default config, 9.5 min)" echo " 6) Custom" echo " 0) Exit" echo "" read -p "Enter choice [0-6]: " choice
case $choice in 1) run_test smoke ;; 2) run_test load ;; 3) run_test stress ;; 4) run_test spike ;; 5) run_test full ;; 6) read -p "Enter VUs: " vus read -p "Enter duration (e.g., 5m): " duration echo -e "${BLUE}Custom Test: ${vus} users for ${duration}${NC}" k6 run --vus ${vus} --duration ${duration} -e K6_BASE_URL=${BASE_URL} ${TEST_FILE} ;; 0) echo -e "${GREEN}Goodbye!${NC}" exit 0 ;; *) echo -e "${RED}Invalid choice${NC}" exit 1 ;; esac }
# Parse command line arguments if [ $# -eq 0 ]; then show_menu else case $1 in smoke|load|stress|spike|full) run_test $1 ;; --help|-h) echo "Usage: $0 [profile]" echo "" echo "Profiles:" echo " smoke - Quick smoke test (10 users, 1 min)" echo " load - Load test (100 users, 5 min)" echo " stress - Stress test (500 users, 10 min)" echo " spike - Spike test (0→1000 users)" echo " full - Full test with default config (9.5 min)" echo "" echo "Environment variables:" echo " K6_BASE_URL - Base URL for API (default: http://localhost:3000)" echo "" echo "Examples:" echo " $0 smoke" echo " K6_BASE_URL=http://localhost:3001 $0 load" ;; *) echo -e "${RED}Unknown profile: $1${NC}" echo "Use --help for usage information" exit 1 ;; esac fi
echo "" echo -e "${GREEN}✓ Test completed${NC}"

import { Request, Response } from "express";
import bcrypt from "bcryptjs";
import jwt from "jsonwebtoken";
import prisma from "../lib/prisma";
import crypto from "crypto";
import {
  loginAttemptsCounter,
  registrationsCounter,
  tokenVerificationsCounter,
  activeSessionsGauge
} from '../lib/metrics';
import { hashPassword, generateToken, generateJti, validateSignup } from '../validations/auth.validations';

// --------------------- Đăng ký customer ---------------------
export const registerCustomer = async (req: Request, res: Response) => {
    try {
        const { email, password, name, phone } = req.body;

        // Validate input using shared validation helper
        const parsed = validateSignup({ name, email, password, phone });
        if (!parsed.success) {
            const err = parsed.error.errors[0];
            const field = err.path && err.path[0] ? String(err.path[0]) : 'input';
            return res.status(400).json({ success: false, message: `${field} ${err.message}` });
        }

        // Kiểm tra email đã tồn tại
        const existingUser = await prisma.user.findUnique({ where: { email } });
        if (existingUser) {
            registrationsCounter.inc({ role: 'CUSTOMER' });
            return res.status(400).json({
                success: false,
                message: "Email already exists",
            });
        }

        // Mã hóa password
        const hashedPassword = await hashPassword(password, 12);

        // Tạo user mới với role CUSTOMER
        const user = await prisma.user.create({
            data: {
                email,
                password: hashedPassword,
                name,
                phone,
                role: "CUSTOMER",
            },
            select: {
                id: true,
                email: true,
                name: true,
                phone: true,
                role: true,
                status: true,
                createdAt: true,
            },
        });
        registrationsCounter.inc({ role: 'CUSTOMER' });

        // Tạo JWT token với jti để hỗ trợ logout
        const jti = generateJti();
        const token = generateToken({ userId: user.id, email: user.email, role: user.role }, jti, '7d');

        res.status(201).json({
            success: true,
            data: { user, token },
            message: "Customer registered successfully",
        });
    } catch (error) {
        console.error("Error registering customer:", error);
        res.status(500).json({
            success: false,
            message: "Error registering",
        });
    }
};

