// --------------------- Đăng xuất ---------------------
export const logout = async (req: Request, res: Response) => {
    try {
        const authHeader = req.headers.authorization;
        const token = authHeader && authHeader.split(" ")[1];
        if (!token) {
            return res.status(400).json({
                success: false,
                message: "Thiếu token để đăng xuất",
            });
        }

        // Xác thực token để lấy thông tin jti và exp
        const decoded = jwt.verify(token, process.env.JWT_SECRET_KEY || "secret") as any;
        const jti: string | undefined = decoded?.jti;
        const exp: number | undefined = decoded?.exp; // giây kể từ epoch
        const userId = (req as any)?.user?.userId as string | undefined;

        const expiresAt = exp ? new Date(exp * 1000) : new Date(Date.now() + 7 * 24 * 60 * 60 * 1000);
        const tokenHash = crypto.createHash("sha256").update(token).digest("hex");

        // Kiểm tra đã thu hồi trước đó chưa (tránh lỗi unique)
        const exists = await prisma.revokedToken.findFirst({
            where: {
                OR: [
                    ...(jti ? [{ jti }] as any[] : []),
                    { tokenHash }
                ],
                expiresAt: { gt: new Date() }
            }
        });

        if (!exists) {
            await prisma.revokedToken.create({
                data: {
                    jti: jti || null,
                    tokenHash,
                    userId: userId || null,
                    expiresAt
                }
            });
        }
        activeSessionsGauge.dec();
        return res.status(200).json({
            success: true,
            message: "Đăng xuất thành công",
        });
    } catch (error: any) {
        // Nếu token hết hạn, vẫn coi là đã đăng xuất thành công (idempotent)
        if (error?.name === "TokenExpiredError") {
            return res.status(200).json({ success: true, message: "Đăng xuất thành công" });
        }
        console.error("Error logging out:", error);
        return res.status(500).json({ success: false, message: "Lỗi khi đăng xuất" });
    }
};

// --------------------- Verify Token (cho API Gateway) ---------------------
export const verifyToken = async (req: Request, res: Response) => {
    try {
        const { token } = req.body;
        if (!token) {
            tokenVerificationsCounter.inc({ status: 'failed' });
            return res.status(400).json({
                success: false,
                message: "Thiếu token"
            });
        }

        // Verify token
        const decoded = jwt.verify(token, process.env.JWT_SECRET_KEY || "secret") as any;

        // Kiểm tra token đã bị thu hồi hay chưa
        const jti = decoded?.jti as string | undefined;
        const tokenHash = crypto.createHash("sha256").update(token).digest("hex");

        const revoked = await prisma.revokedToken.findFirst({
            where: {
                AND: [
                    {
                        OR: [
                            ...(jti ? ([{ jti }] as any[]) : []),
                            { tokenHash }
                        ]
                    },
                    { expiresAt: { gt: new Date() } }
                ]
            }
        });

        if (revoked) {
            return res.status(401).json({
                success: false,
                message: "Token đã bị thu hồi"
            });
        }

        // Kiểm tra user còn tồn tại và active
        const userId = decoded?.userId as string | undefined;
        const email = decoded?.email as string | undefined;
        const role = decoded?.role as string | undefined;

        if (!userId || !email || !role) {
            return res.status(401).json({
                success: false,
                message: "Token không hợp lệ"
            });
        }

        const user = await prisma.user.findUnique({ where: { id: userId } });
        if (!user || user.status !== "ACTIVE") {
            return res.status(403).json({
                success: false,
                message: "Tài khoản đã bị khóa hoặc không tồn tại"
            });
        }

        // Token hợp lệ
        tokenVerificationsCounter.inc({ status: 'success' });
        return res.status(200).json({
            success: true,
            data: {
                userId,
                email,
                role
            }
        });
    } catch (err: any) {
        tokenVerificationsCounter.inc({ status: 'failed' });
        const message = err?.name === "TokenExpiredError" ? "Token đã hết hạn" : "Token không hợp lệ";
        return res.status(401).json({ success: false, message });
    }
};

import bcrypt from 'bcryptjs';
import jwt from 'jsonwebtoken';

export async function hashPassword(password: string): Promise<string> {
  const saltRounds = 10;
  return bcrypt.hash(password, saltRounds);
}

export async function comparePassword(password: string, hash: string): Promise<boolean> {
  return bcrypt.compare(password, hash);
}

export function generateToken(payload: Record<string, any>): string {
  const secret = process.env.JWT_SECRET as string | undefined;
  return jwt.sign(payload, secret as any, { expiresIn: '7d' });
}

export function verifyToken(token: string): any {
  const secret = process.env.JWT_SECRET as string | undefined;
  return jwt.verify(token, secret as any);
}

import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Star, Plus, Minus } from "lucide-react";
import { useCart } from "@/contexts/cart-context";

interface MenuItem {
  id: number;
  name: string;
  description: string;
  price: number;
  image: string;
  popular: boolean;
}

interface MenuSectionData {
  category: string;
  items: MenuItem[];
}

interface MenuSectionProps {
  section: MenuSectionData;
  restaurantId: number;
  restaurantName: string;
}

const MenuSection = ({ section, restaurantId, restaurantName }: MenuSectionProps) => {
  const { state, dispatch } = useCart();

  const formatPrice = (price: number) => {
    return new Intl.NumberFormat('vi-VN', {
      style: 'currency',
      currency: 'VND'
    }).format(price);
  };

  const handleAddToCart = (item: MenuItem) => {
    dispatch({
      type: "ADD_ITEM",
      payload: {
        id: item.id.toString(),
        name: item.name,
        price: item.price,
        imageUrl: item.image,
      },
    });
  };

  const handleUpdateQuantity = (itemId: string, quantity: number) => {
    dispatch({
      type: "UPDATE_QUANTITY",
      payload: { id: itemId, quantity },
    });
  };

  const getQuantityInCart = (itemId: string) => {
    const cartItem = state.items.find((item) => item.id === itemId);
    return cartItem?.quantity || 0;
  };

  return (
    <div className="mb-8">
      <h2 className="text-2xl font-bold text-foreground mb-4">{section.category}</h2>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {section.items.map((item) => {
          const quantity = getQuantityInCart(item.id.toString());

          return (
            <Card key={item.id} className="group cursor-pointer overflow-hidden hover:shadow-lg transition-all duration-300">
              <div className="flex">
                {/* Item Info */}
                <CardContent className="flex-1 p-4">
                  <div className="flex items-start justify-between mb-2">
                    <div className="flex-1">
                      <div className="flex items-center gap-2 mb-1">
                        <h3 className="font-semibold text-lg text-foreground group-hover:text-primary transition-colors">
                          {item.name}
                        </h3>
                        {item.popular && (
                          <Badge className="bg-orange-100 text-orange-800 text-xs">
                            <Star className="w-3 h-3 mr-1 fill-current" />
                            Phổ biến
                          </Badge>
                        )}
                      </div>

                      <p className="text-sm text-muted-foreground mb-3 line-clamp-2">
                        {item.description}
                      </p>

                      <p className="text-xl font-bold text-primary mb-3">
                        {formatPrice(item.price)}
                      </p>
                    </div>
                  </div>

                  {/* Add to Cart Controls */}
                  <div className="flex items-center justify-between">
                    <div className="text-sm text-muted-foreground">
                      Giao trong 15-20 phút
                    </div>

                    {quantity === 0 ? (
                      <Button
                        size="sm"
                        onClick={() => handleAddToCart(item)}
                        className="h-8 px-4"
                      >
                        <Plus className="w-4 h-4 mr-1" />
                        Thêm
                      </Button>
                    ) : (
                      <div className="flex items-center gap-2">
                        <Button
                          variant="outline"
                          size="sm"
                          className="h-8 w-8 p-0"
                          onClick={() => handleUpdateQuantity(item.id.toString(), quantity - 1)}
                        >
                          <Minus className="w-3 h-3" />
                        </Button>
                        <span className="font-semibold min-w-[2rem] text-center">
                          {quantity}
                        </span>
                        <Button
                          size="sm"
                          className="h-8 w-8 p-0"
                          onClick={() => handleUpdateQuantity(item.id.toString(), quantity + 1)}
                        >
                          <Plus className="w-3 h-3" />
                        </Button>
                      </div>
                    )}
                  </div>
                </CardContent>

                {/* Item Image */}
                <div className="w-24 md:w-32 h-24 md:h-32 relative flex-shrink-0">
                  <img
                    src={item.image}
                    alt={item.name}
                    className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-300"
                  />
                  {item.popular && (
                    <div className="absolute top-1 right-1">
                      <Badge className="bg-primary text-primary-foreground text-xs px-1 py-0">
                        HOT
                      </Badge>
                    </div>
                  )}
                </div>
              </div>
            </Card>
          );
        })}
      </div>
    </div>
  );
};

export default MenuSection;

import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { Button } from "@/components/ui/button";
import { MapPin, Phone, Clock, Package } from "lucide-react";
import { useState } from "react";
import OtpVerificationDialog from "./OtpVerificationDialog";

interface OrderDetailDialogProps {
  open: boolean;
  onClose: () => void;
  order: any; // Order type from MerchantOrdersPage
}

const OrderDetailDialog = ({ open, onClose, order }: OrderDetailDialogProps) => {
  const [showOtpDialog, setShowOtpDialog] = useState(false);

  if (!order) return null;

  const getStatusBadge = (status: string) => {
    const statusMap: Record<string, { label: string; className: string }> = {
      new: { label: "Mới", className: "bg-red-500" },
      confirmed: { label: "Đã xác nhận", className: "bg-blue-500" },
      preparing: { label: "Đang chuẩn bị", className: "bg-yellow-500" },
      ready: { label: "Sẵn sàng", className: "bg-green-500" },
      delivering: { label: "Đang giao", className: "bg-purple-500" },
      completed: { label: "Hoàn thành", className: "bg-gray-500" }
    };

    const { label, className } = statusMap[status] || { label: status, className: "bg-gray-400" };
    return <Badge className={className}>{label}</Badge>;
  };

  return (
    <>
      <Dialog open={open} onOpenChange={onClose}>
        <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle className="flex items-center justify-between">
              <span>Chi tiết đơn hàng #{order.id}</span>
              {getStatusBadge(order.status)}
            </DialogTitle>
          </DialogHeader>

          <div className="space-y-4">
            {/* Customer Info */}
            <div className="bg-gray-50 rounded-lg p-4 space-y-3">
              <h3 className="font-semibold text-sm text-gray-700">Thông tin khách hàng</h3>
              <div className="space-y-2">
                <div className="flex items-center gap-2 text-sm">
                  <Package className="h-4 w-4 text-gray-500" />
                  <span className="font-medium">{order.customerName}</span>
                </div>
                <div className="flex items-center gap-2 text-sm text-gray-600">
                  <Phone className="h-4 w-4" />
                  <span>{order.phone}</span>
                </div>
                <div className="flex items-start gap-2 text-sm text-gray-600">
                  <MapPin className="h-4 w-4 mt-0.5 flex-shrink-0" />
                  <span>{order.address}</span>
                </div>
              </div>
            </div>

            <Separator />

            {/* Order Items */}
            <div>
              <h3 className="font-semibold text-sm text-gray-700 mb-3">Các món ăn</h3>
              <div className="space-y-2">
                {order.items?.map((item: any, idx: number) => (
                  <div key={idx} className="flex items-center justify-between text-sm bg-white border rounded-lg p-3">
                    <div className="flex-1">
                      <p className="font-medium">{item.name}</p>
                      <p className="text-gray-500">x{item.quantity}</p>
                    </div>
                    <span className="font-semibold text-primary">
                      {item.price.toLocaleString("vi-VN")}đ
                    </span>
                  </div>
                ))}
              </div>
            </div>

            <Separator />

            {/* Total */}
            <div className="bg-primary/5 rounded-lg p-4">
              <div className="flex items-center justify-between">
                <span className="font-semibold text-lg">Tổng cộng</span>
                <span className="font-bold text-xl text-primary">
                  {order.total.toLocaleString("vi-VN")}đ
                </span>
              </div>
            </div>

            {/* Order Time */}
            <div className="flex items-center gap-2 text-sm text-gray-600">
              <Clock className="h-4 w-4" />
              <span>Thời gian đặt: {order.createdAt}</span>
            </div>

            <Separator />

            {/* Actions */}
            <div className="space-y-3">
              {/* Show OTP verification button if drone arrived */}
              {order.droneArrived && order.status === 'preparing' && (
                <div className="bg-green-50 border border-green-200 rounded-lg p-4 space-y-3">
                  <div className="flex items-center gap-2 text-green-700 font-medium">
                    <span className="text-2xl"></span>
                    <span>Drone đã đến! Vui lòng xác nhận nhận hàng</span>
                  </div>
                  <Button 
                    onClick={() => setShowOtpDialog(true)}
                    className="w-full bg-green-600 hover:bg-green-700"
                  >
                     Nhập mã OTP để xác nhận
                  </Button>
                </div>
              )}

              {/* Show notify button if preparing and no drone arrived */}
              {order.status === 'preparing' && !order.droneArrived && order.onNotifyReady && (
                <Button
                  onClick={() => order.onNotifyReady(order.restaurantOrderId)}
                  disabled={order.notifying}
                  className="w-full bg-indigo-600 hover:bg-indigo-700"
                >
                  {order.notifying ? '⏳ Đang thông báo...' : ' Thông báo đội giao (Ready)'}
                </Button>
              )}

              {/* Close button */}
              <Button variant="outline" onClick={onClose} className="w-full">
                Đóng
              </Button>
            </div>
          </div>
        </DialogContent>
      </Dialog>

      {/* OTP Verification Dialog */}
      {showOtpDialog && (
        <OtpVerificationDialog
          open={showOtpDialog}
          onClose={() => setShowOtpDialog(false)}
          deliveryId={order.deliveryId || order.restaurantOrderId}
          orderId={order.restaurantOrderId}
          onSuccess={() => {
            setShowOtpDialog(false);
            onClose();
            // Reload orders will be triggered by parent
            if (order.onOtpVerified) {
              order.onOtpVerified();
            }
          }}
        />
      )}
    </>
  );
};

export default OrderDetailDialog;
